import { useState } from 'react'
import { editInvite } from '../../../Data/Database'
import { Formik, Field, Form } from 'formik'
import { Button } from '../../Common/Static'

export default function RsvpEdit(props) {
  const [serverError, setServerError] = useState(false)

  return (
    <>
      <Formik
        initialValues={{
          name: props.person.name,
        }}
        onSubmit={(values) => {
          editInvite(values.name, props.auth)
            .then((data) => {
                props.editPerson(props.person.name,values.name)
                props.setEditForm()
            })
            .catch(() => {
              setServerError(true)
            })
        }}
      >
        {() => (
          <Form>
            {serverError ? (
              <p>Server may be down!! Please try again later.</p>
            ) : null}
            <Field
              type='text'
              name='name'
              className='name'
              placeholder='name'
            />
            <Button type='submit' text='Edit' className='submitButton'/>
          </Form>
        )}
      </Formik>
    </>
  )
}
