import React, { useState } from 'react'
import RsvpListItem from './RsvpListItem'

export default function RsvpList(props) {
  const [search, setSearch] = useState('')

  const list = props.list.forEach((person) => {
    if (search !== '') {
      let reg = new RegExp(`${search}\\w`,'g')
      let name = person.name.toLowerCase().replace(/\s/g, '')
      let found = name.match(reg)
      if (found) {
        console.log(found)
        return (
          <RsvpListItem
            person={person}
            key={person.name}
            setSelected={props.setSelected}
            toggleList={props.toggleList}
          />
        )
      }
    }
    return (
      <RsvpListItem
        person={person}
        key={person.name}
        setSelected={props.setSelected}
        toggleList={props.toggleList}
      />
    )
  })

  function handleChange(event) {
    let found = event.target.value.toLowerCase()
    setSearch(found)
  }

  return (
    <>
      <input
        className='search'
        type='text'
        value={search}
        onChange={(event) => handleChange(event)}
        disabled={false}
        placeholder='Search'
      />

      <ul className='rsvpList'>{list}</ul>
    </>
  )
}
