import React from 'react';
import Loader from "react-loader-spinner";
import axios from 'axios';
import { API } from '../../constants';

class RSVPform extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: props.name,
      email: props.email,
      response: props.response,
      text: '',
      loading: false
    }
    this.handelSubmit = this.handelSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.checkAuth = this.checkAuth.bind(this);
  }

  checkAuth() {
    axios({
      method: 'POST',
      baseURL: API + 'rsvp',
      data: {
        name: this.state.name,
        email: this.state.email,
        response: this.state.response,
        text: this.state.text
      }
    }).then(() => {
      this.setState({
        loading: false
      })
      this.props.rsvp(this.state.email, this.state.name, this.state.response);
    }).catch((err) => {
      this.setState({
        serverError: true,
        loading: false
      })
      console.log(err);
    });

  }

  handelSubmit(event) {
    event.preventDefault();
    this.setState({
      loading: true
    })
    this.checkAuth();
  };

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    })
  }

  render() {
    if (this.state.loading) {
      return (
        <div className="loader">
          <Loader type="TailSpin" color="#00BFFF" height={80} width={80} />
        </div>
      )
    } else {
      return (
        <form onSubmit={this.handelSubmit}>
          {this.state.serverError ? <p>Server may be down!! Please try again later.</p> : null}
          <input className="name" name="name" type="text" value={this.state.name} onChange={this.handleChange} />
          <input className="email" placeholder="email" name="email" type="email" value={this.state.email} onChange={this.handleChange} />
          <label className="question" for="attending">Will you be attending?</label>
          <select id="response" className="response" name="response" value={this.state.response} onChange={this.handleChange}>
            <option value="maybe">Maybe</option>
            <option value="yes">Yes</option>
            <option value="no">No</option>
          </select>
          <textarea placeholder="Any questions?" value={this.state.text} onChange={this.handleChange} />
          <button className="rsvpButton" type="submit" value="submit">RSVP</button>
        </form >
      );
    }
  }
}

export default RSVPform;
