import React from 'react';
import Loader from "react-loader-spinner";
import axios from 'axios';
import { API } from '../../constants';

class RSVPform extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: props.name,
      email: props.email,
      response: props.response,
      text: '',
      loading: false,
      serverError: false,
      noEmail: false
    }
    this.handelSubmit = this.handelSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.checkAuth = this.checkAuth.bind(this);
  }

  checkAuth() {
    axios({
      method: 'POST',
      baseURL: API + 'rsvp',
      data: {
        name: this.state.name,
        email: this.state.email,
        response: this.state.response,
        text: this.state.text
      }
    }).then(() => {
      this.setState({
        loading: false
      })
      this.props.rsvp(this.state.email, this.state.name, this.state.response);
    }).catch((err) => {
      this.setState({
        serverError: true,
        loading: false
      })
      console.log(err);
    });

  }

  handelSubmit(event) {
    event.preventDefault();
    if ((this.state.response === "yes") & (this.state.email === "")) {
      console.log(this.state.email);
      this.setState({
        noEmail: true
      })
    } else {
      this.setState({
        loading: true
      })
      this.checkAuth();
    }
  };

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
    })
  }

  render() {
    if (this.state.loading) {
      return (
        <div className="loader">
          <Loader type="TailSpin" color="#00BFFF" height={80} width={80} />
        </div>
      )
    } else {
      return (
        <>
          <h2>RSVP</h2>
          {this.props.openRsvp ?
            <form onSubmit={this.handelSubmit}>
              {this.state.serverError ?
                <p>Server may be down!! Please try again later.</p>
                :
                null}
              {this.state.noEmail & (this.state.response === "yes") ?
                <p>No email!!! Please enter a email if planning to attend.</p>
                :
                null}
              <input className="name"
                name="name"
                type="text"
                value={this.state.name}
                onChange={this.handleChange} />
              <input className="email"
                placeholder="email"
                name="email"
                type="email"
                value={this.state.email}
                onChange={this.handleChange} />
              <label className="question" for="attending">Will you be attending?</label>
              <select id="response" className="response" name="response" value={this.state.response} onChange={this.handleChange}>
                <option value="maybe">Maybe</option>
                <option value="yes">Yes</option>
                <option value="no">No</option>
              </select>
              <textarea placeholder="Any questions?" name="text" value={this.state.text} onChange={this.handleChange} />
              <button className="rsvpButton" type="submit" value="submit">RSVP</button>
            </form >
            :
            <div className="formClosed">
              <p className="note">Thank you for responding.</p>
              <button className="rsvpChange"
                onClick={this.props.openForm}>
                Change RSVP </button>
            </div>
          }
        </>
      );
    }
  }
}

export default RSVPform;
