import React from 'react';
import axios from 'axios';
import { API } from '../constants';

class SignInForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            serverError: false,
            nameError: false
        };

        this.handelSubmit = this.handelSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.checkAuth = this.checkAuth.bind(this);
    }

    checkAuth() {
        axios({
            method: 'POST',
            baseURL: API + 'signin',
            data: {
                name: this.state.name
            }
        }).then((data) => {
            console.log(data);
            if (data.status === 203) {
                this.setState({
                    nameError: true
                })
            } else {
                this.props.logIn(data.headers.authorization, this.state.name);
            }
        }).catch((err) => {
            this.setState({
                serverError: true
            })
            console.log(err);
        });
    }

    handelSubmit(event) {
        event.preventDefault();
        this.checkAuth();
    };

    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value,
            nameError: false,
            serverError: false
        })
    }

    render() {
        return (
            <form onSubmit={this.handelSubmit}>
                {this.state.nameError ? <p>{this.state.name} does not match list of invites, Please try again</p> : null}
                {this.state.seagainrverError ? <p>Server may be down!! Please try again later.</p> : null}
                <input placeholder="name" name="name" type="text" value={this.state.name} onChange={this.handleChange} />
                <button type="submit" value="submit">Join</button>
            </form>
        );
    }
}

export default SignInForm;
