import React, { useState } from 'react'
import RsvpListItem from './RsvpListItem'

export default function RsvpList(props) {
  const [search, setSearch] = useState('')

  const list = props.list.forEach((person) => {
    let found = person.name.toLowerCase().replace(/\s/g, '').match(search)
    if (found.length > 0) {
      console.log(person)
      return <RsvpListItem
        person={person}
        key={person.name}
        setSelected={props.setSelected}
        toggleList={props.toggleList}
      />
    }
  })

  function handleChange(event) {
    let found = event.target.value.toLowerCase()
    setSearch(`/${found}*/g`)
    console.log(search)
  }

  return (
    <>
      <input
        className='search'
        type='text'
        value={search}
        onChange={(event) => handleChange(event)}
        disabled={false}
        placeholder='Search'
      />

      <ul className='rsvpList'>{list}</ul>
    </>
  )
}
