import React, { useEffect, useState } from "react"
import RsvpIndividual from './RsvpIndividual';
import RsvpListItem from './RsvpListItem';
import { GetList } from '../../../Data/Database';

export default function RsvpAdmin(props) {

    const [individual, setIndividual] = useState(false);
    const [selected, setSelected] = useState({});
    const [list, setList] = useState([]);

    useEffect(() => {
        GetList(props.auth)
            .then(data => {
                setList(data.data);
            })
            .catch(err => {
                console.log("Error :", err);
            })
    })

    return (
        individual ? <RsvpIndividual selected={selected} /> :
            <ul>
                {list.forEach(person => {
                    <RsvpListItem setSelected={setSelected}
                        setIndividual={setIndividual}
                        person={person} />
                })
                }
            </ul>
    )
}
