import React from 'react';

class RsvpForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            email: '',
            name: '',
            rsvp: false
        };
        this.handelSubmit = this.handelSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.checkAuth = this.checkAuth.bind(this);
    }

    checkAuth() {
    }

    handelSubmit(event) {
        event.preventDefault();
        this.checkAuth();
    };

    handleChange(event) {
        console.log(event.target.value);
        this.setState({
            rsvp: event.target.value
        });
        this.forceUpdate();
    }

    render() {
        return (
            <form onSubmit={this.handelSubmit}>
                <label htmlFor="attending">
                    Will you be attending our wedding?
                    <select value={this.state.rsvp} onChange={this.handleChange}>
                        <option value={true}>Yes</option>
                        <option value={false}>No</option>
                    </select>
                </label>
                {this.state.rsvp ?
                    <>
                        <label htmlFor="name">Name:
                            <input name="name" type="text" value={this.state.name} onChange={this.handleChange} />
                        </label>
                        <label htmlFor="email">Email:
                            <input name="email" type="email" value={this.state.name} onChange={this.handleChange} />
                        </label>
                        <button className="signInButton" type="submit" value="submit">Confirm</button>
                    </>
                    : <button className="signInButton" type="submit" value="submit">Confirm</button>
                }
            </form>
        );
    }
}

export default RsvpForm;
