import './styles/App.scss';

import Footer from './structure/Footer';
import Header from './structure/Header';
import Main from './structure/Main';
import SignInPage from './structure/signInPage';
import { PAGES } from './constants';
import React from 'react';

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      signedIn: false,
      current: PAGES.WEDDING,
      auth: '',
      name: '',
      response: 'maybe',
      email: '',
      openRsvp: true
    }
    this.logIn = this.logIn.bind(this);
    this.changeScreen = this.changeScreen.bind(this);
    this.rsvp = this.rsvp.bind(this);
    this.openForm = this.openForm.bind(this);
  }

  logIn(data, name, body) {
    this.setState({
      signedIn: true,
      auth: data,
      name: name,
      email: body.email,
      response: body.rsvp
    });
  }

  rsvp(email, name, response) {
    this.setState({
      name: name,
      email: email,
      response: response,
      openRsvp: response === "yes" ? false :
        response === "no" ? false : true
    });
  }

  openForm() {
    this.setState({
      openRsvp: true
    })
  }

  changeScreen(selection) {
    this.setState({
      current: selection
    });
  }


  render() {
    if (!this.state.signedIn) {
      return (
        <div className="signedOut" >
          <SignInPage
            logIn={this.logIn}
          />
        </div >
      )
    } else {
      return (
        <div className="signedIn">
          <Header
            menuSelection={this.changeScreen}
            selection={this.state.current} />
          <Main
            selection={this.state.current}
            name={this.state.name}
            rsvp={this.rsvp}
            email={this.state.email}
            response={this.state.response}
            openForm={this.openForm}
            openRsvp={this.state.openRsvp} />
          <Footer />
        </div >
      );
    }

  }

}

export default App;
