import React from 'react';
import Countdown from "../Header/Countdown";
import SignInButton from '../SignIn/SignInButton';
import SignInForm from '../SignIn/signInForm';


class SignInPage extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            openForm: false
        }

        this.toggleForm = this.toggleForm.bind(this);
    }

    toggleForm() {
        this.setState({
            openForm: !this.state.openForm
        })
    }

    render() {
        return (
            <div className="container">
                <Countdown />
                <div className="message">
                    <p>Paul</p><p>&</p><p>Elyse</p>
                </div>
                {this.state.openForm ?
                    <SignInForm logIn={this.props.logIn} /> :
                    <SignInButton toggleForm={this.toggleForm} />}
                <p className="contact">Having trouble signining in? Contact Us at <p><i>paulandelysewedding@gmail.com</i></p></p>
                <p className="photocredit">Photo Credit: Brittany Fleming - Facebook: @bflemingphoto Instagram: @bflemingphotography</p>
            </div>
        );
    }

}

export default SignInPage;
