  const [nameError, setNameError] = useState(false)
  const [serverError, setServerError] = useState(false)

  return (
    <Formik
      initialValues={{
        name: ''
      }}
      onSubmit={(values) => { 
        AddInvite(values.name,auth)
          .then((data) => {
            if (data.status === 203) {
              setNameError(true)
            } else {
            }
          })
          .catch(() => {
            setServerError(true)
          })
      }}
    >
      {({ values }) => (
        <Form>
