import React from 'react';
import RsvpInfo from './components/rsvpInfo';

class RSVP extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            rsvp: false
        }

        this.changeRsvp = this.changeRsvp.bind(this);
    }

    changeRsvp(event) {
        this.setState({rsvp: event.target.value});      
    }


    render() {    
        return ( <div className="rsvp">
                    <h2>RSVP</h2>
                    <form>
                        <select defaultValue={this.state.rsvp} onChange={this.changeRsvp}>
                            <option value={true}>Yes</option>
                            <option value={false}>No</option>
                        </select>
                        {this.state.rsvp ? <RsvpInfo />: null}     
                    </form>
                </div> )
    }
}

export default RSVP;
