import RSVPform from '../components/Main/RsvpForm';
import React from 'react';

class RSVP extends React.Component {

    render() {
        return (
            <div className="rsvp">
                <h2>RSVP</h2>
                {this.props.openRsvp ?
                    <RSVPform name={this.props.name} rsvp={this.props.rsvp} /> :
                    this.props.response === "yes" ?
                        <>
                            <p>Thank you for responding. We will contact you soon</p>
                            <button className="rsvpChange" >Change RSVP</button>
                        </> :
                        <>
                            <p>Thank you for responding.</p>
                            <button className="rsvpChange" onClick={this.props.openForm}>Change RSVP</button>
                        </>}
            </div>
        );
    }

}

export default RSVP;
