import React, { useEffect, useState } from 'react'
import RsvpIndividual from './RsvpIndividual'
import RsvpList from './RsvpList'
import RsvpAdd from './RsvpAdd'
import { GetList, RemovePerson, AddInvite } from '../../../Data/Database'
import { Button } from '../../Common/Static'

export default function RsvpAdmin(props) {
  const [individual, setIndividual] = useState(false)
  const [selected, setSelected] = useState({})
  const [list, setList] = useState([])
  const [openForm, setOpenForm] = useState(false)
  const [removeError, setRemoveError] = useState(false)
  const [removed, setRemoved] = useState('')
  const [serverError, setServerError] = useState(false)
  const [addedPerson, setAddedPerson] = useState('')
  const [addedPersonError, setAddedPersonError] = useState('')

  function toggleList() {
    setIndividual(!individual)
  }
  function toggleForm() {
    setOpenForm(!openForm)
  }
  function removePerson(person) {
    RemovePerson(person)
      .then((data) => {
        if (data.statusCode === 203) {
          setRemoveError(person.name)
        } else {
          setIndividual(false)
          setRemoved(person.name)
          list.filter((listPerson) => listPerson.name !== person.name)
          setList(list)
        }
      })
      .catch(() => {
        setServerError(true)
      })
  }
  function addPerson(person, err) {
    if (!err) {
      setAddedPerson(person.name)
      list.push(person)
      openForm(false)
    } else if (err === 500) {
      setServerError(true)
    } else {
      setAddedPersonError(person.name)
    }
  }

  useEffect(() => {
    GetList(props.auth)
      .then((data) => {
        setList(JSON.parse(data.data))
      })
      .catch((err) => {
        console.log('Error :', err)
      })
  }, [props.auth])

  return (
    <>
      <h2>
        {openForm
          ? 'Add Rsvp'
          : individual
          ? selected.name + ' Rsvp'
          : 'Rsvp List'}
      </h2>
      <div className='rsvpadmin'>
        {removed !== '' ? <p>{removed} has been removed</p> : null}
        {addedPerson !== '' ? <p>{addedPerson} has been added</p> : null}
        {addedPersonError !== '' ? (
          <p>{addedPersonError} already is there</p>
        ) : null}
        {removeError !== '' ? null : <p>{removeError} does not exist</p>}
        {serverError ? <p>There is a server error</p> : null}
        {openForm ? (
          <RsvpAdd
            auth={props.auth}
            toggleForm={toggleForm}
            addPerson={addPerson}
          />
        ) : individual ? (
          <RsvpIndividual
            toggleList={toggleList}
            selected={selected}
            removePerson={removePerson}
          />
        ) : (
          <RsvpList
            setSelected={setSelected}
            list={list}
            toggleList={toggleList}
          />
        )}
        {openForm ? null : (
          <Button
            text='Add Invite'
            clickAction={toggleForm}
            className='addInviteButton'
          />
        )}
      </div>
    </>
  )
}
