import React from 'react'
import { Button } from '../../Common/Static'

export default function RsvpIndividual(props) {
  const [removeError, setRemoveError] = useState(false)
  const [removed, setRemoved] = useState('')

  function removePerson(person) {
    RemovePerson(person)
      .then((data) => {
        if (data.statusCode === 203) {
          setRemoveError(person.name)
        } else {
          setIndividual(false)
          setRemoved(person.name)
          list.filter((listPerson) => listPerson.name !== person.name)
          setList(list)
        }
      })
      .catch(() => {
        setServerError(true)
      })
  }

  return (
    <>
      {removed !== '' ? <p>{removed} has been removed</p> : null}
      {removeError !== '' ? <p>{removeError} does not exist</p> : null}

      <div className='individual'>
        <p>{props.selected.name}</p>
        <p>{props.selected.email === '' ? 'NA' : props.selected.email}</p>
        <p>{props.selected.response}</p>
        <Button
          text='Back to List'
          clickAction={() => props.toggleList()}
          className='backButton'
        />
        <Button
          text='Remove Invite'
          clickAction={() => removePerson(props.selected.name)}
          className='removeInvite'
        />
      </div>
    </>
  )
}
