import React, { useEffect, useState } from 'react'
import RsvpIndividual from './RsvpIndividual'
import RsvpList from './RsvpList'
import RsvpAdd from './RsvpAdd'
import { GetList, RemovePerson } from '../../../Data/Database'
import { Button } from '../../Common/Static'

export default function RsvpAdmin(props) {
  const [individual, setIndividual] = useState(false)
  const [selected, setSelected] = useState({})
  const [list, setList] = useState([])
  const [openForm, setOpenForm] = useState(false)
  const [removeError, setRemoveError] = useState(false)
  const [notice, setNotice] = useState('')

  function toggleList(person) {
    setIndividual(!individual)
    setSelected(person)
  }
  function toggleForm() {
    setOpenForm(!openForm)
  }
  function removePerson(person) {
    RemovePerson(person.name)
      .then((data) => {
        if (data.statusCode == 203) {
          setRemoveError(person.name)
        } else {
          list.filter((listPerson) => listPerson.name != person.name)
          setList(list)
          setIndividual(false)
          setNotice(person.name)
        }
      })
      .catch(() => {
        setServerError(true)
      })
  }

  useEffect(() => {
    GetList(props.auth)
      .then((data) => {
        setList(JSON.parse(data.data))
      })
      .catch((err) => {
        console.log('Error :', err)
      })
  }, [props.auth])

  return (
    <>
      <h2>
        {individual
          ? selected.name + 'Rsvp'
          : openForm
          ? 'Add Invite'
          : 'Rsvp List'}
      </h2>
      {notice != '' ? <p>{notice} has been removed</p>:null}
      {openForm ? (
        <RsvpAdd auth={props.auth} toggleForm={toggleForm} />
      ) : individual ? (
        <RsvpIndividual
          setSelected={toggleList}
          selected={selected}
          removePerson={removePerson}
        />
      ) : (
        <RsvpList setSelected={toggleList} list={list} />
      )}
      {openForm ? (
        <Button text='Back' clickAction={toggleForm} />
      ) : (
        <Button text='Add Invite' clickAction={toggleForm} />
      )}
    </>
  )
}
