import React, { useState, useEffect } from 'react'
import RsvpListItem from './RsvpListItem'

export default function RsvpList(props) {
  const [search, setSearch] = useState('')
  const [display, setDisplay] = useState([])

  useEffect(() => {
    let newlist = props.list.filter((person) => {
      let name = person.name.toLowerCase().replace(/\s/g, '')
      if (search === '') {
console.log("no Search:",person)
        return (
          <RsvpListItem
            person={person}
            key={person.name}
            setSelected={props.setSelected}
            toggleList={props.toggleList}
          />
        )
      } else if ((search !== '') & name.match(search)) {
         console.log("Search:",person)
        return (
          <RsvpListItem
            person={person}
            key={person.name}
            setSelected={props.setSelected}
            toggleList={props.toggleList}
          />
        )

      } else {
        return null
      }
    })
    console.log(newlist)
    setDisplay(newlist)
  },[props.list, props.setSelected, props.toggleList, search])

  function handleChange(event) {
    let found = event.target.value.toLowerCase()
    setSearch(new RegExp(`${found}\\w*`, 'g'))
  }

  return (
    <>
      <input
        className='search'
        type='text'
        value={search}
        onChange={(event) => handleChange(event)}
        disabled={false}
        placeholder='Search'
      />

      <ul className='rsvpList'>{display}</ul>
    </>
  )
}
