import { useState } from 'react';
import { CheckAuth } from '../../Data/Database';
import { Formik, Field, Form } from 'formik';

export default function SignInForm(props) {

  const [nameError, setNameError] = useState(false);
  const [serverError, setServerError] = useState(false);

  return (
    <Formik
      intialValues={{
        name: props.name,
        password: ''
      }}
      onSubmit={(values) => {
        CheckAuth(values.name, values.password).
          then((data) => {
            if (data.status === 203) {
              setNameError(true);
            } else {
              props.setAuth(data.headers.authorization);
              props.setName(values.name);
            }
          }).catch(() => {
            setServerError(true);
          })
      }}
    >
      {(props) => {
        <Form>
          {nameError ? <p>
            "{props.values.name}" does not match list of invites, Please try again</p>
            : null}
          {serverError ? <p>
            Server may be down!! Please try again later.</p>
            : null}
          <Field type="text" name="name"
            className="name" placeholder="name"
          />
          {props.values.name === 'admin' ?
            <Field type="password" name="password"
              className="password" placeholder="password" />
            : null}
          <button className="signInButton"
            type="submit"
            disabled={props.isSubmitting}>Join</button>
        </Form>
      }}
    </Formik>
  );
}
