import React, { useState, useEffect } from 'react'
import RsvpListItem from './RsvpListItem'

export default function RsvpList(props) {
  const [search, setSearch] = useState('')
  const [display, setDisplay] = useState([])

  useEffect(() => {
    let newlist = props.list.filter((person) => {
      if (search !== '' & name.match(search)) {
        let name = person.name.toLowerCase().replace(/\s/g, '')
        let found = name.match(search)
        if (found) {
          return (
            <RsvpListItem
              person={person}
              key={person.name}
              setSelected={props.setSelected}
              toggleList={props.toggleList}
            />
          )
        }
      } else {
        return (
          <RsvpListItem
            person={person}
            key={person.name}
            setSelected={props.setSelected}
            toggleList={props.toggleList}
          />
        )
      }
    })
    setDisplay(newlist)
  },[search])

  function handleChange(event) {
    let found = event.target.value.toLowerCase()
    setSearch(new RegExp(`${search}\\w*`, 'g'))
  }

  return (
    <>
      <input
        className='search'
        type='text'
        value={search}
        onChange={(event) => handleChange(event)}
        disabled={false}
        placeholder='Search'
      />

      <ul className='rsvpList'>{display}</ul>
    </>
  )
}
