import React, { useState, useEffect } from 'react';
import { Rsvp, GetRsvp } from '../../../Data/Database';
import { Formik, Field, Form } from 'formik';

export default function RSVPform(props) {

    const [email, setEmail] = useState('');
    const [serverError, setServerError] = useState(false);

    useEffect(() => {
        GetRsvp(props.auth)
            .then(data => {
                setEmail(data.data);
            })
            .catch((err) => {
                console.log("Error :", err);
                setServerError(true);
            });
    }, [props.auth]);

    return (
        <>
            <h2>RSVP</h2>
            <Formik
                enableReinitialize={true}
                initialValues={{
                    email: email,
                    response: props.response,
                    text: ''
                }}
                validate={values => {
                    const errors = {};
                    if (!values.email && (values.response === "yes")) {
                        errors.email = 'Email Required';
                    } else if (
                        !/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email) &&
                        (values.response === "yes")
                    ) {
                        errors.email = 'Invalid email address';
                    }
                    return errors;
                }}
                onSubmit={values => {
                    Rsvp({
                        email: values.email,
                        response: values.response,
                        text: values.text
                    }, props.auth).then(() => {
                        props.setResponse(values.response);
                        props.toggleForm;
                    }).catch((err) => {
                        console.log(err);
                        setServerError(true);
                    });
                }}
            >
                {({ errors, isSubmitting }) => (
                    <Form >
                        {serverError ? <p>Server may be down!! Please try again later.</p>
                            : null}
                        {errors.email ?
                            <p>{errors.email}</p> : null}
                        <Field type="email" name="email"
                            className="email"
                            placeholder="email" />
                        <label className="question">
                            Will you be attending?</label>
                        <Field className="response" as="select" name="response" >
                            <option value="maybe">Maybe</option>
                            <option value="yes">Yes</option>
                            <option value="no">No</option>
                        </Field>
                        <Field as="textarea" name="text"
                            placeholder="Any Questions?" />
                        <button className="rsvpButton"
                            type="submit"
                            disabled={isSubmitting}
                        >RSVP</button>
                    </Form>
                )}
            </Formik>
        </>);
}
