import RSVPform from '../components/Main/RsvpForm';
import React from 'react';

class RSVP extends React.Component {

    render() {
        return (
            <div className="rsvp">
                <h2>RSVP</h2>
                {this.props.openRsvp ?
                    <RSVPform
                        name={this.props.name}
                        rsvp={this.props.rsvp}
                        email={this.props.email}
                        response={this.props.response} /> :
                    <div className="formClosed">
                        {this.props.response === "yes" ?
                            <p className="message">Thank you for responding. We will contact you soon</p> :
                            <p className="message">Thank you for responding.</p>
                        }
                        <p className="response">Current response {this.props.response}</p>
                        <button className="rsvpChange" onClick={this.props.openForm}>Change RSVP</button>
                    </div>
                }
            </div>
        );
    }

}

export default RSVP;
