import RSVPform from './Rsvp/RsvpForm';
import RsvpAdmin from './Rsvp/RsvpAdmin';
import RsvpClosed from './Rsvp/RsvpClosed';
import React, { useState, useEffect } from 'react';

export default function RSVP(props) {

    const [openRsvpForm, setOpenRsvpForm] = useState(false);

    useEffect(() => {
        if (props.response === "maybe") setOpenRsvpForm(true);
    }, [props.response])

    return (
        < div className="rsvp" >
            {props.name === "admin" ? <RsvpAdmin /> :
                openRsvpForm ? <RSVPform
                    auth={props.auth}
                    response={props.response}
                    setOpenRsvpForm={setOpenRsvpForm}
                    setResponse={props.setResponse} /> :
                    <RsvpClosed setOpenRsvpForm={setOpenRsvpForm} />
            }
        </div >
    );
}
