import React from 'react';
import Loader from "react-loader-spinner";
import DataSource from '../../Data/DataSource';

class SignInForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: '',
      password: '',
      serverError: false,
      nameError: false,
      loading: false
    };

    this.handelSubmit = this.handelSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.checkAuth = this.checkAuth.bind(this);
  }

  handelSubmit(event) {
    event.preventDefault();
    this.setState({
      loading: true
    })
    DataSource.checkAuth();
  };

  handleChange(event) {
    this.setState({
      [event.target.name]: event.target.value,
      nameError: false,
      serverError: false
    })
  }

  render() {
    if (this.state.loading) {
      return (
        <div className="loader">
          <Loader type="TailSpin" color="#00BFFF" height={80} width={80} />
        </div>
      )
    } else {
      return (
        <form onSubmit={this.handelSubmit}>
          {this.state.nameError ? <p>"{this.state.name}" does not match list of invites, Please try again</p> : null}
          {this.state.serverError ? <p>Server may be down!! Please try again later.</p> : null}
          <input className="name" placeholder="name"
            name="name"
            type="text"
            value={this.state.name}
            onChange={this.handleChange} />
          {this.state.name === "admin" ?
            <input className="password" placeholder="password"
              name="password"
              type="password"
              value={this.state.password}
              onChange={this.handleChange} /> : null}
          <button className="signInButton" type="submit" value="submit">Join</button>
        </form>
      );
    }
  }
}

export default SignInForm;
