import { API } from '../constants';
import axios from 'axios';

class DataSource {

  static CheckAuth(url, name, password) {
    axios({
      method: 'POST',
      baseURL: API + url,
      data: {
        name: name,
        password: password
      }
    }).then((data) => {
      if (data.status === 203) {
        this.setState({
          nameError: true,
          loading: false
        })
      } else {
        this.setState({
          loading: false
        })
        this.props.logIn(data.headers.authorization, this.state.name, data.data);
      }
    }).catch((err) => {
      this.setState({
        serverError: true,
        loading: false
      })
      console.log(err);
    });
  }
}

export default DataSource;
