import React, { useEffect, useState } from "react"
import RsvpIndividual from './RsvpIndividual';
import RsvpList from './RsvpList';
import { GetList } from '../../../Data/Database';

export default function RsvpAdmin(props) {

    const [individual, setIndividual] = useState(false);
    const [selected, setSelected] = useState({});
    const [list, setList] = useState([]);
    const [numPersons, setNumPersons] = useState(0);

    function toggleIndividual() {
        setIndividual(!individual);
    }

    useEffect(() => {
        GetList(props.auth)
            .then(data => {
                setList(data.data);
                setNumPersons(data.data.size());
            })
            .catch(err => {
                console.log("Error :", err);
            })
    }, [])

    return (
        <>
            <h2>{individual ? selected.name + " Rsvp" : "Rsvp List"}</h2>
            {
                individual ?
                    <RsvpIndividual toggleIndividual={toggleIndividual}
                        selected={selected} />
                    : <RsvpList setSelected={setSelected}
                        toggleIndividual={toggleIndividual}
                        list={list} />
            }
        </>
    )
}
