import { API } from '../constants';
import axios from 'axios';

class DataSource {

  static CheckAuth(name, password, setNameError, setServerError, setAuth) {
    axios({
      method: 'POST',
      baseURL: API + 'signin',
      data: {
        name: name,
        password: password
      }
    }).then((data) => {
      if (data.status === 203) {
        setNameError(true);
      } else {
        setAuth(data.headers.authorization)
        this.props.logIn(data.headers.authorization, this.state.name, data.data);
      }
    }).catch((err) => {
      setServerError(true);
      console.log(err);
    });
  }
}

export default DataSource;
