import React, { useEffect, useState } from 'react'
import RsvpIndividual from './RsvpIndividual'
import RsvpList from './RsvpList'
import { GetList } from '../../../Data/Database'
import { Button } from '../Layout/Common/Static'

export default function RsvpAdmin(props) {
  const [individual, setIndividual] = useState(false)
  const [selected, setSelected] = useState({})
  const [list, setList] = useState([])
  const [openForm,setOpenForm] = useState(false)

  function toggleList(person) {
    setIndividual(!individual)
    setSelected(person)
  }
  function toggleForm() {
    setOpenForm(!openForm)
  }

  useEffect(() => {
    GetList(props.auth)
      .then((data) => {
        setList(JSON.parse(data.data))
      })
      .catch((err) => {
        console.log('Error :', err)
      })
  }, [props.auth])

  return (
    <>
      <h2>{individual ? selected.name + ' Rsvp' : 'Rsvp List'}</h2>
      {individual ? (
        <RsvpIndividual setSelected={toggleList} selected={selected} />
      ) : (
        <RsvpList setSelected={toggleList} list={list} />
      )}
      <Button text='Add Invite' clickAction={ToggleForm} />
    </>
  )
}
