import React from 'react';
import { PAGES } from '../../constants';

class Menu extends React.Component {

  render() {
    return (<div className="options">
      <p className={this.props.selection === PAGES.WEDDING ? "selected" : null} onClick={() => this.props.menuSelection(PAGES.WEDDING)}>Wedding</p>
      <p className={this.props.selection === PAGES.TRAVEL ? "selected" : null} onClick={() => this.props.menuSelection(PAGES.TRAVEL)}>Travel</p>
      <p className={this.props.selection === PAGES.RSVP ? "selected" : null} onClick={() => this.props.menuSelection(PAGES.RSVP)}>RSVP</p>
    </div>);
  }
}

export default Menu;
