import { useState, useEffect } from 'react';
import Loader from "react-loader-spinner";
import DataSource from '../../Data/DataSource';

function SignInForm(props) {

  const [serverError, setServerError] = useState(false);
  const [password, setPassword] = useState();
  const [nameError, setNameError] = useState(false);
  const [loading, setLoading] = useState(false);

  function handleSubmit(e) {
    e.preventDefault();
    setLoading(true);
    DataSource.CheckAuth();
  }


  if (loading) {
    return (
      <div className="loader">
        <Loader type="TailSpin" color="#00BFFF" height={80} width={80} />
      </div>
    )
  } else {
    return (
      <form onSubmit={handleSubmit}>
        {nameError ? <p>
          "{props.name}" does not match list of invites, Please try again</p>
          : null}
        {serverError ? <p>
          Server may be down!! Please try again later.</p>
          : null}
        <input className="name" placeholder="name"
          name="name"
          type="text"
          value={props.name}
          onChange={(e) => props.setName(e.target.value)} />
        {props.name === "admin" ?
          <input className="password" placeholder="password"
            name="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)} /> : null}
        <button className="signInButton" type="submit" value="submit">Join</button>
      </form>
    );
  }
}
export default SignInForm;
