import React, { useState } from 'react';
import { Rsvp, GetRsvp } from '../../../Data/Database';
import { Formik, Field, Form } from 'formik';

export default function RSVPform(props) {

    const [email, setEmail] = useState('');
    const [response, setResponse] = useState("maybe");
    const [text, setText] = useState('');
    const [serverError, setServerError] = useState(false);

    useEffect(() => {
        GetRsvp(props.name).
            then(data => {

            }).catch(() => {
                setServerError(true);
            })
    })

    return (
        <>
            <h2>RSVP</h2>
            <Formik
                initialValues={{
                    name: props.name,
                    email: email,
                    response: response,
                    text: text
                }}
                validate={values => {
                    const errors = {};
                    if (!values.email) {
                        errors.email = 'Email Required';
                    } else if (!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i.test(values.email)) {
                        errors.email = 'Invalid email address';
                    }
                    return errors;
                }}
                onSubmit={values => {
                    Rsvp({
                        name: values.name,
                        email: values.email,
                        response: values.response,
                        text: values.text
                    }).then(() => {
                        props.setOpenRsvpForm(false);
                    }).catch(() => {
                        setServerError(true);
                    });
                }}
            >
                {({ values, errors, isSubmitting }) => (
                    <Form >
                        {serverError ? <p>Server may be down!! Please try again later.</p>
                            : null}
                        {errors.email && (values.response === "yes") ?
                            <p>{errors.email}</p> : null}
                        <Field type="text" name="name"
                            className="name" />
                        <Field type="email" name="email"
                            className="email"
                            placeholder="email" />
                        <label className="question">
                            Will you be attending?</label>
                        <Field className="response" as="select" name="response" >
                            <option value="maybe">Maybe</option>
                            <option value="yes">Yes</option>
                            <option value="no">No</option>
                        </Field>
                        <Field as="textarea" name="text"
                            placeholder="Any Questions?" />
                        <button className="rsvpButton"
                            type="submit"
                            disabled={isSubmitting}
                        >RSVP</button>
                    </Form>
                )}
            </Formik>
        </>);
}
