import React from 'react'
import { useState } from 'react'
import { Button } from '../../Common/Static'
import { editInvite } from '../../../Data/Database'

export default function RsvpIndividual(props) {
  const [editForm, setEditForm] = useState(false)
  const [newName, setNewName] = useState('')

  function handleChange(event) {
    setNewName(event.target.value)
  }

  return (
    <>
      <div className='individual'>
        {editForm ? (
          <>
            <div className='infoDisplay'>
              <input
                type='text'
                value={props.selected.name}
                onChange={handleChange}
              />
              <input
                type='text'
                value={props.selected.email}
                disabled={true}
              />
              <input
                type='text'
                value={props.selected.response}
                disabled={true}
              />
            </div>
            <Button
              text='Save'
              clickAction={() => editInvite(props.selected.name, newName)}
              className='editInvite'
            />
            <Button
              text='Back to List'
              clickAction={() => props.toggleList()}
              className='backButton'
            />
            <Button
              text='Back'
              clickAction={() => setEditForm(false)}
              className='removeInvite'
            />
          </>
        ) : (
          <>
            <div className='infoDisplay'>
             <input
                type='text'
                value={props.selected.name}
                onChange={handleChange}
              />
              <input
                type='text'
                value={props.selected.email}
                disabled={true}
              />
              <input
                type='text'
                value={props.selected.response}
                disabled={true}
              /> 
            </div>
            <Button
              text='Edit Invite'
              clickAction={() => setEditForm(true)}
              className='editInvite'
            />
            <Button
              text='Back to List'
              clickAction={() => props.toggleList()}
              className='backButton'
            />
            <Button
              text='Remove Invite'
              clickAction={() => props.removePerson(props.selected.name)}
              className='removeInvite'
            />
          </>
        )}
      </div>
    </>
  )
}
