import './styles/App.scss';

import Footer from './structure/Footer';
import Header from './structure/Header';
import Main from './structure/Main';
import SignInPage from './structure/signInPage';
import { PAGES } from './constants';
import React from 'react';

let images = [
  "../images/211004-Elyse-Paul-000002.jpg",
  "../images/211004-Elyse-Paul-000126.jpg",
  "../images/211004-Elyse-Paul-000243.jpg"
]

class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      signedIn: false,
      current: PAGES.WEDDING,
      auth: '',
      name: '',
      image: "url(" + images[0] + ")"
    }
    this.logIn = this.logIn.bind(this);
    this.changeScreen = this.changeScreen.bind(this);
  }

  componentDidMount() {
    this.interval = setInterval(() => {
      this.setState({ image: "url(" + images[1] + ")" });
    }, 1000);
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  logIn(data, name) {
    this.setState({
      signedIn: true,
      auth: data,
      name: name
    });
  }

  changeScreen(selection) {
    this.setState({
      current: selection
    });
  }


  render() {
    if (!this.state.signedIn) {
      return (
        <div className="signedOut" style={{ backgroundImage: this.state.image }}>
          <SignInPage
            logIn={this.logIn}
          />
        </div >
      )
    } else {
      return (
        <div className="signedIn" style={{ backgroundImage: this.state.image }}>
          <Header
            menuSelection={this.changeScreen}
            selection={this.state.current} />
          <Main
            auth={this.state.auth}
            selection={this.state.current} />
          <Footer />
        </div>
      );
    }

  }

}

export default App;
