import  React  from 'react';

class SignInForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            error: false
        };

        this.handelSubmit = this.handelSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.checkAuth = this.checkAuth.bind(this);
    }

    checkAuth() {
        fetch('auth.json',{
            headers: {
                'Content-Type' : 'application/json',
                'Accept' : 'application/json'
            }
        }).then(response => {
            return response.json();
        }).then(response => {
            for (let people of response) {
                if(this.state.name.toLowerCase() === people.name.toLowerCase()){
                    this.props.logIn();
                    return;
                };
            };
            this.setState({error: true});
        })
    };


    handelSubmit(event) {
        event.preventDefault();
        this.checkAuth();
    };

    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value,
            error: false
        })

    }

    render(){ 
        return (
            <form onSubmit={this.handelSubmit}>
                {this.state.error ? <p>Name does not macth list of invites, Please try again</p> : null}
                <input placeholder="name" name="name" type="text" value={this.state.name} onChange={this.handleChange}/>
                <button>Enter</button>
            </form>
        );
    }
}

export default SignInForm;
