import React from 'react';
import axios from 'axios';
import { API } from '../../constants';

class SignInForm extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: '',
            error: false
        };

        this.handelSubmit = this.handelSubmit.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.checkAuth = this.checkAuth.bind(this);
    }

    checkAuth() {
        axios({
            method: 'POST',
            baseURL: API + 'signin',
            data: {
                name: this.state.name
            }
        }).then((data) => {
            console.log(data);
            this.props.logIn(data.headers.authorization);
        }).catch((err) => {
            this.setState({
                error: true
            })
            console.log(err);
        });
    }

    handelSubmit(event) {
        event.preventDefault();
        this.checkAuth();
    };

    handleChange(event) {
        this.setState({
            [event.target.name]: event.target.value,
            error: false
        })
    }

    render() {
        return (
            <form onSubmit={this.handelSubmit}>
                {this.state.error ? <p>{this.state.name} does not match list of invites, Please try again</p> : null}
                <input placeholder="name" name="name" type="text" value={this.state.name} onChange={this.handleChange} />
                <button type="submit" value="submit">Enter</button>
            </form>
        );
    }
}

export default SignInForm;
